import React, {useState, useEffect} from 'react'
import { NavLink } from 'react-router-dom'
import axios from 'axios'
import API_URL from '../../util/API_URL'

const Student = () => {
  let [allStu,setAlltu]= useState([]);

  useEffect(()=>{
    getData();
  },[])

  let getData = async ()=>{
    let result = await axios.get(`${API_URL}`);
    setAlltu(result.data);
  }

  return (
   <div className="row">
    <div className="col-md-6 offset-md-3 mt-3">
        <NavLink className="btn btn-info" to="/student/add ">Add New Student</NavLink>
        <table className='table table-dark my-3'>
          <thead>
           <tr>
              <td>S.no</td>
              <td>Name</td>
              <td>Fee</td>
              <td>Gender</td>
              <td>Address</td>
             <td>Edit</td>
             <td>Delete</td>
            </tr>
          </thead>
          <tbody>
{
  allStu.map((item,index)=>{
    return(
    <tr key={item.id}>
      <td>{index+1}</td>
      <td>{item.name}</td>
      <td>{item.fee}</td>
      <td>{item.gender}</td>
      <td>{item.address}</td>
      <td><button className='btn btn-sm btn-info'>Edit</button></td>
              <td><button data-bs-toggle className='btn btn-sm btn-danger'>Delete</button></td>
      </tr>
    )
  })
}
          </tbody>
        </table>
    </div>
   </div>
  )
}

export default Student
