import React from 'react'
import {useFormik} from 'formik'
import axios from 'axios'
import * as YUP from 'yup'
import API_URL from '../../util/API_URL'
import { useNavigate ,useParams} from 'react-router-dom'

let addfrmSchema=YUP.object({
  name:YUP.string().required("insert your name"),
  fee:YUP.string().required("enter fee"),
  gender:YUP.string().required("select gender"),
  address:YUP.string().required("insert address"),
})

const AllStudent = () => {
  let navigate=useNavigate();
  let param =useParams();

    let addFrm=useFormik({
      validationSchema:addfrmSchema,
        initialValues:{
            name:"",
            fee:"",
            gender:"",
            address:""
        },
        onSubmit:async(formdata)=>{
          let result = await axios.post(`${API_URL}`, formdata);
          navigate("/student")
        }
    })

    
  return (
   <>
   <div className="row">
    <div className="col-md-6 offset-md-3 mt-3"> 
  <form onSubmit={addFrm.handleSubmit}>
    <h4>{param.id? 'Update' : 'Add New Student' }</h4>
    <div className='m-2'>
      <label >Name</label>
      <input type="text" name='name' onChange={addFrm.handleChange} className={'form-control '+(addFrm.errors.name && addFrm.touched.name ? 'is-invalid' : '')}/>
      {
        addFrm.errors.name && addFrm.touched.name?<small className='text-danger'>{addFrm.errors.name}</small>:""
      }
      </div>
      <div className='m-2'>
      <label >Fee</label>
      <input type="text" name='fee' onChange={addFrm.handleChange} className={'form-control '+(addFrm.errors.fee && addFrm.touched.fee ? 'is-invalid' : '')}/>
      {
        addFrm.errors.fee && addFrm.touched.fee?<small className='text-danger'>{addFrm.errors.fee}</small>:""
      }
      </div>
      <div classfee='m-2'>
      <label >Gender</label>
      <br />
      Male<input type="radio" onChange={addFrm.handleChange} name='gender' value='male'/>
      Female<input type="radio" onChange={addFrm.handleChange} name='gender' value='female '/>
      {
        addFrm.errors.gender && addFrm.touched.gender?<small className='text-danger'>{addFrm.errors.gender}</small>:""
      }
      </div>
      <div className='m-2'>
      <label >Address</label>
      <textarea type="text"  name='address' onChange={addFrm.handleChange} className={'form-control '+(addFrm.errors.address && addFrm.touched.address ? 'is-invalid' : '')}></textarea>
      {
        addFrm.errors.address && addFrm.touched.address?<small className='text-danger'>{addFrm.errors.address}</small>:""
      }
       </div>
       <button type='submit' className='btn btn-info m-2'> {param.id? 'Update' : 'Add' }</button>
       </form>
      </div>
   </div>
   </>
  )
}

export default AllStudent
